import React, { useState, useEffect } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import Login from './Project/Login';
import Signup from './Project/Signup';
import Home from './Project/Home';
import AboutUs from './Project/AboutUs';
import ContactUs from './Project/ContactUs';
import Footer from './Project/Footer';
import Setting from './Project/Setting';
import Security from './Project/Security';
import Navbar from './Project/Navbar';
import FoodFest from './Project/FoodFest';
import BookVendor from './Project/BookVendor';
import BookingDetails from './Project/BookingDetails';

function App() {
    const [userName, setUserName] = useState(null);

    useEffect(() => {
        // Fetch user info from the backend based on token
        const fetchUser = async () => {
            const token = localStorage.getItem('token');
            if (token) {
                try {
                    const response = await fetch('/api/users/me', {
                        method: 'GET',
                        headers: {
                            'Authorization': `Bearer ${token}`
                        }
                    });
                    const data = await response.json();
                    setUserName(data.name || 'Guest');
                } catch (error) {
                    console.error('Error fetching user info:', error);
                    setUserName('Guest');
                }
            } else {
                setUserName('Guest');
            }
        };
        fetchUser();
    }, []);

    const handleLogout = () => {
        setUserName('Guest');
    };

    return (
        <BrowserRouter>
            <Navbar userName={userName} onLogout={handleLogout} />
            <Routes>
                <Route path='/' element={<Home />} />
                <Route path='/AboutUs' element={<AboutUs />} />
                <Route path='/ContactUs' element={<ContactUs />} />
                <Route path='/Setting' element={<Setting />} />
                <Route path='/Security' element={<Security />} />
                <Route path='/foodfest' element={<FoodFest />} />
                <Route path='/bookvendor' element={<BookVendor />} />
                <Route path='/bookingdetails' element={<BookingDetails />} />
                <Route path='/login' element={<Login />} />
                <Route path='/signup' element={<Signup />} />
            </Routes>
            <Footer />
        </BrowserRouter>
    );
}

export default App;
