import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
    const navigate = useNavigate(); 
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [rememberMe, setRememberMe] = useState(false);
    const [message, setMessage] = useState('');

    const handleRememberMeChange = (e) => {
        setRememberMe(e.target.checked);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:5000/api/login', {
                email,
                password
            });

            if (response.data.success) {
                setMessage('Login successful');
                navigate('/Home'); // Redirect to home or any other page upon successful login
            } else {
                setMessage('Invalid email or password');
            }
        } catch (error) {
            setMessage('Error logging in. Please try again.');
            console.error('Login error:', error);
        }
    };

    return (
        <div className='container d-flex justify-content-center align-items-center vh-100'>
            <div className="form-container login-panel d-flex justify-content-evenly align-items-center p-5 shadow rounded bg-white w-100 vh-50">
                <div className='image-container left object-fit img-fluid'>
                    <p className="mt-3">Don't have an account? <Link to="/Signup" className="text-decoration-none">Sign Up</Link></p>
                </div>
                <div className="d-flex flex-column justify-content-center align-items-center rounded shadow p-5 bg-light login-page">
                    <h2 className='display-4 p-3'>Log In</h2>
                    <form onSubmit={handleSubmit} className="w-100">
                        <div className="mb-3">
                            <label className="form-label">Email:</label>
                            <input 
                                type="email" 
                                value={email} 
                                onChange={(e) => setEmail(e.target.value)} 
                                className="form-control" 
                                required 
                            />
                        </div>
                        <div className="mb-3">
                            <label className="form-label">Password:</label>
                            <input 
                                type="password" 
                                value={password} 
                                onChange={(e) => setPassword(e.target.value)} 
                                className="form-control" 
                                required 
                            />
                        </div>
                        <div className="mb-3 form-check">
                            <input 
                                type="checkbox" 
                                checked={rememberMe} 
                                onChange={handleRememberMeChange} 
                                className="form-check-input" 
                                id="rememberMe" 
                            />
                            <label className="form-check-label" htmlFor="rememberMe">Remember me</label>
                        </div>
                        <button type="submit" className="btn w-100">Login</button>
                        <hr className='mt-5 mb-4 border-secondary-subtle' />
                        <div className="text-end">
                            <a href="#!" className="link-secondary text-decoration-none">Forgot password</a>
                        </div>
                    </form>
                    <p>{message}</p> {/* Display login status message */}
                </div>
            </div>
        </div>
    );
}

export default Login;
