import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import Login from './Project/Login';
import Signup from './Project/Signup';
import Home from './Project/Home';
import AboutUs from './Project/AboutUs';
import ContactUs from './Project/ContactUs';
import Footer from './Project/Footer';
import Setting from './Project/Setting';
import Security from './Project/Security';
import Navbar from './Project/Navbar';
import FoodFest from './Project/FoodFest';
import BookVendor from './Project/BookVendor';
import BookingDetails from './Project/BookingDetails';

const App = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
      const token = localStorage.getItem('token');
      const userId = localStorage.getItem('userId');
      if (token && userId) {
          try {
              const decoded = jwtDecode(token);
              setUser(decoded);  // Set user from decoded token
          } catch (error) {
              console.error('Invalid token:', error);
              localStorage.removeItem('token');
              localStorage.removeItem('userId');
          }
      }
  }, []);

  const handleLogout = () => {
      localStorage.removeItem('token');
      localStorage.removeItem('userId');
      setUser(null);
  };
    return (
        <Router>
            <Navbar user={user} /> {/* Navbar displayed across all routes */}
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/login" element={<Login setUser={setUser} />} />
                <Route path="/signup" element={<Signup setUser={setUser} />} />
                <Route path="/home" element={<Home />} />
                <Route path="/foodfest" element={<FoodFest />} />
                <Route path="/bookvendor" element={<BookVendor />} />
                <Route path="/bookingdetails/:id" element={<BookingDetails />} />
                <Route path="/setting" element={user ? <Setting /> : <Navigate to="/login" />} />
                <Route path="/security" element={user ? <Security /> : <Navigate to="/login" />} />
                <Route path="/aboutus" element={<AboutUs />} />
                <Route path="/contactus" element={<ContactUs />} />
                <Route path="/footer" element={<Footer />} />
            </Routes>
        </Router>
    );
};

export default App;
