import React, { useState, useEffect } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import axios from 'axios'; // For fetching user data
import Login from './Project/Login';
import Signup from './Project/Signup';
import Home from './Project/Home';
import AboutUs from './Project/AboutUs';
import ContactUs from './Project/ContactUs';
import Footer from './Project/Footer';
import Setting from './Project/Setting';
import Security from './Project/Security';
import Navbar from './Project/Navbar';
import FoodFest from './Project/FoodFest';
import BookVendor from './Project/BookVendor';
import BookingDetails from './Project/BookingDetails';

function App() {
  const [user, setUser] = useState(null); // State to store user data
  const userId = localStorage.getItem('userId'); // Fetch user ID from localStorage

  useEffect(() => {
    if (userId) {
      // Fetch user data using the ID from localStorage
      axios.get(`http://localhost:5000/api/users/${userId}`)
        .then(response => {
          setUser(response.data); // Set user data from API
        })
        .catch(error => {
          console.error('Error fetching user data:', error);
        });
    }
  }, [userId]); // Dependency array ensures this runs only when userId changes

  return (
    <BrowserRouter>
      {/* Pass the user state to the Navbar */}
      <Navbar user={user} />

      {/* Routes for different pages */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/home" element={<Home />} />
        <Route path="/foodfest" element={<FoodFest />} />
        <Route path="/bookvendor" element={<BookVendor />} />
        <Route path="/bookingdetails/:id" element={<BookingDetails />} />
        <Route path="/setting" element={<Setting />} />
        <Route path="/security" element={<Security />} />
        <Route path="/aboutus" element={<AboutUs />} />
        <Route path="/contactus" element={<ContactUs />} />
        <Route path="/footer" element={<Footer />} />
      </Routes>

      {/* Footer at the bottom of every page */}
      <Footer />
    </BrowserRouter>
  );
}

export default App;
