import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import AOS from 'aos';
import 'aos/dist/aos.css';

function Login() {

    const navigate = useNavigate(); // Initialize the useNavigate hook

    AOS.init({
        duration: 1200, 
        once: false, 
        mirror: false, 
    });

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [rememberMe, setRememberMe] = useState(false);

    const handleRememberMeChange = (e) => {
        setRememberMe(e.target.checked);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        console.log("Email:", email);
        console.log("Password:", password);

        // Proceed with your login logic
        navigate('/', { state: { loginSuccess: true } });
        alert("Login Successful");
    };

    return (
        <div className='container d-flex justify-content-center align-items-center vh-100' data-aos="flip-left">
            <div className="row w-100 p-5 shadow rounded bg-white">
                <div className="col-lg-6  image-container">
                    <img src='logofav.jpg' alt="login-image" className="img-fluid"/>
                    <p className="mt-3">Don't have an account? <Link to="/Signup" className="text-decoration-none">Sign Up</Link></p>
                </div>
                <div className="col-lg-6 col-md-12 d-flex flex-column justify-content-center align-items-center p-5 bg-light" data-aos="fade-down">
                    <h2 className='display-4 mb-4'>Log In</h2>
                    <form onSubmit={handleSubmit} className="w-100" data-aos="fade-up">
                        <div className="mb-3">
                            <label className="form-label">Email:</label>
                            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} className="form-control" required />
                        </div>
                        <div className="mb-3">
                            <label className="form-label">Password:</label>
                            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} className="form-control" required />
                        </div>
                        <div className="mb-3 form-check">
                            <label className="form-check-label" htmlFor="rememberMe">Remember me</label>
                            <input type="checkbox" checked={rememberMe} onChange={handleRememberMeChange} className="form-check-input" id="rememberMe" />
                        </div>
                        <button type="submit" className="btn btn-primary w-100">Login</button>
                        <hr className='mt-5 mb-4 border-secondary-subtle' />
                        <div className="text-end">
                            <a href="#!" className="link-secondary text-decoration-none">Forgot password</a>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Login;
