import React, { useState } from 'react';
import axios from 'axios';
import './Security.css'; // Assuming you have some styles

const Security = () => {
    const [oldPassword, setOldPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    // Function to handle password change
    const handleChangePassword = async (event) => {
        event.preventDefault();

        // Client-side validation
        if (newPassword !== confirmPassword) {
            setErrorMessage('New password and confirm password do not match');
            return;
        }

        try {
            const response = await axios.put('/api/change-password', {
                oldPassword: oldPassword,
                newPassword: newPassword,
            });

            // Check if response and data exist before accessing data
            if (response && response.data) {
                setSuccessMessage('Password changed successfully');
                setErrorMessage('');
            } else {
                setErrorMessage('Unexpected response from server');
            }
        } catch (error) {
            // Handle error (network issues, incorrect old password, etc.)
            if (error.response && error.response.data) {
                setErrorMessage(error.response.data.message || 'Error changing password');
            } else {
                setErrorMessage('An unexpected error occurred');
            }
        }
    };

    return (
        <div className="security-container">
            <h2>Change Password</h2>
            <form onSubmit={handleChangePassword}>
                <div className="form-group">
                    <label htmlFor="oldPassword">Old Password</label>
                    <input
                        type="password"
                        id="oldPassword"
                        className="form-control"
                        value={oldPassword}
                        onChange={(e) => setOldPassword(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="newPassword">New Password</label>
                    <input
                        type="password"
                        id="newPassword"
                        className="form-control"
                        value={newPassword}
                        onChange={(e) => setNewPassword(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="confirmPassword">Confirm New Password</label>
                    <input
                        type="password"
                        id="confirmPassword"
                        className="form-control"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        required
                    />
                </div>

                {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
                {successMessage && <div className="alert alert-success">{successMessage}</div>}

                <button type="submit" className="btn btn-primary">Change Password</button>
            </form>
        </div>
    );
};

export default Security;
