import React, { useEffect, useState } from 'react';

function FoodFest() {
    const [vendors, setVendors] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetch('http://localhost:5000/api/vendors')
            .then(response => response.json())
            .then(data => {
                setVendors(data);
                setLoading(false);
            })
            .catch(error => {
                setError('Failed to fetch vendors');
                setLoading(false);
            });
    }, []);

    if (loading) {
        return <p>Loading...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    return (
        <div>
            <section className='header'>
                <img className='banner' src='images/upcoming-events (3).png' alt='food-fest-banner' />
                <img src="images/upcoming-events (2).png" alt="Overlay" className="overlay-image" />
            </section>

            <section className='search'>
                <div className='container mt-5 '>
                    <div className='col-md-12 text-center'>
                        <h2 className='font-weight-bold display-5'>Discover more events</h2>
                        <p className='text-muted'>
                            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                        </p>
                        <form className="d-flex" role="search">
                            <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
                            <button className="btn btn-outline-primary" type="submit">Search</button>
                        </form>
                    </div>
                </div>
            </section>

            <section className='event-list'>
                <div className='container mt-5 mb-5'>
                    <div className="row">
                        {vendors.map(vendor => (
                            <div className="col-md-3" key={vendor._id} data-aos="fade-up">
                                <div className="card text-center foodfest">
                                    <div className="card-body">
                                        <img className='card-img-top' src={`http://localhost:5000/api/image/${vendor.image}`} alt={vendor.name} />
                                        <h5 className="card-title text-center mt-2 fs-4">{vendor.name}</h5>
                                        <p className="card-text">{vendor.description}</p>
                                        <div className='btn btn-primary text-center'>View Details</div>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </section>
        </div>
    );
}

export default FoodFest;
