import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Setting() {
  const [darkMode, setDarkMode] = useState(false);

  const handleDarkModeToggle = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className={darkMode ? 'dark-mode' : ''}>
      <div>
        <div className="container-xl px-4 mt-4">
          <nav className="nav nav-borders">
            <Link className="nav-link active ms-0" to="/Setting">Profile</Link>
            <Link className="nav-link" to="/Security">Security</Link>
          </nav>
          <hr className="mt-0 mb-4" />
          <div className="row">
            {/* ... */}
          </div>
          <div className="col-xl-8">
            <div className="card mb-4">
              <div className="card-header">Account Details</div>
              <div className="card-body">
                {/* ... */}
              </div>
            </div>
            <div className="card mb-4">
              <div className="card-header">Theme</div>
              <div className="card-body">
                <h5 className="mb-1">Dark Mode</h5>
                <p className="small text-muted">
                  Toggle dark mode on and off.
                </p>
                <div className="form-check">
                  <input
                    type="checkbox"
                    id="dark-mode-checkbox"
                    checked={darkMode}
                    onChange={handleDarkModeToggle}
                  />
                  <label htmlFor="dark-mode-checkbox">Dark Mode</label>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Setting;
