import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function Security({ user }) {
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleChangePassword = async (event) => {
    event.preventDefault();

    try {
        const response = await axios.put('/api/change-password', {
            oldPassword: oldPassword,  // Assuming these are state variables
            newPassword: newPassword,
        });

        // Check if response and data exist before accessing data
        if (response && response.data) {
            console.log('Password changed successfully:', response.data);
            // You can show a success message or redirect the user
        } else {
            console.error('Unexpected response format', response);
        }
    } catch (error) {
        // Handle error (network issues, incorrect old password, etc.)
        if (error.response && error.response.data) {
            console.error('Error changing password:', error.response.data);
            // Display error message to user
        } else {
            console.error('Unexpected error:', error);
        }
    }
};

  return (
    <div className="container-xl px-4 mt-4">
      <nav className="nav nav-borders">
        <Link className="nav-link ms-0" to="/Setting">Profile</Link>
        <Link className="nav-link active" to="/Security">Security</Link>
      </nav>
      <hr className="mt-0 mb-4" />
      <div className="row">
        <div className="col-lg-8">
          <div className="card mb-4">
            <div className="card-header">Change Password</div>
            <div className="card-body">
              <form>
                <div className="mb-3">
                  <label className="small mb-1" htmlFor="currentPassword">Current Password</label>
                  <input
                    className="form-control"
                    id="currentPassword"
                    type="password"
                    placeholder="Enter current password"
                    value={currentPassword}
                    onChange={(e) => setCurrentPassword(e.target.value)}
                  />
                </div>
                <div className="mb-3">
                  <label className="small mb-1" htmlFor="newPassword">New Password</label>
                  <input
                    className="form-control"
                    id="newPassword"
                    type="password"
                    placeholder="Enter new password"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                  />
                </div>
                <div className="mb-3">
                  <label className="small mb-1" htmlFor="confirmPassword">Confirm Password</label>
                  <input
                    className="form-control"
                    id="confirmPassword"
                    type="password"
                    placeholder="Confirm new password"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                  />
                </div>
                {message && <div className="alert alert-info">{message}</div>}
                <button
                  className="btn"
                  type="button"
                  onClick={handleChangePassword}
                >
                  Save
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Security;
