import React, { useEffect, useState } from 'react';

function FoodFest() {
    const [vendors, setVendors] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [file, setFile] = useState(null);
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [uploadedFile, setUploadedFile] = useState(null);

    // Fetch vendors from backend API when the component mounts
    useEffect(() => {
        fetchVendors();
    }, []);

    // Fetch Vendors from Backend
    const fetchVendors = () => {
        fetch('http://localhost:5000/api/vendors')
            .then(response => response.json())
            .then(data => {
                setVendors(data);
                setLoading(false);
            })
            .catch(error => {
                setError('Failed to fetch vendors');
                setLoading(false);
            });
    };

    // Handle File Change
    const onFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    // Handle Form Submit for Image Upload and Vendor Creation
    const onFormSubmit = async (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append('file', file);

        try {
            // Upload image to GridFS
            const response = await fetch('http://localhost:5000/api/upload', {
                method: 'POST',
                body: formData,
            });
            const data = await response.json();
            const imageFilename = data.file.filename;

            // Now send the vendor details to the server
            const vendorResponse = await fetch('http://localhost:5000/api/vendors', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    name,
                    description,
                    image: imageFilename,
                }),
            });

            const vendorData = await vendorResponse.json();
            setUploadedFile(vendorData.vendor);

            // Fetch the updated list of vendors after adding a new one
            fetchVendors();

            // Reset form fields
            setName('');
            setDescription('');
            setFile(null);
        } catch (error) {
            console.error('Error uploading the file', error);
        }
    };

    if (loading) {
        return <p>Loading...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    return (
        <div>
            {/* Section for Image Upload and Vendor Submission */}
            <section className='upload-form'>
                <h2>Upload Vendor Image</h2>
                <form onSubmit={onFormSubmit}>
                    <input
                        type="text"
                        placeholder="Vendor Name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                    <input
                        type="text"
                        placeholder="Vendor Description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        required
                    />
                    <input type="file" onChange={onFileChange} required />
                    <button type="submit">Upload Vendor</button>
                </form>
            </section>

            {/* Section for Displaying Vendors */}
            <section className='event-list'>
                <div className='container mt-5 mb-5'>
                    <div className="row">
                        {vendors.map(vendor => (
                            <div className="col-md-3" key={vendor._id}>
                                <div className="card text-center foodfest">
                                    <div className="card-body">
                                        <img className='card-img-top' src={`http://localhost:5000/api/image/${vendor.image}`} alt={vendor.name} />
                                        <h5 className="card-title text-center mt-2 fs-4">{vendor.name}</h5>
                                        <p className="card-text">{vendor.description}</p>
                                        <div className='btn btn-primary text-center'>View Details</div>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </section>

            {/* Show Uploaded Vendor */}
            {uploadedFile && (
                <section className='uploaded-vendor'>
                    <h3>Uploaded Vendor:</h3>
                    <p>Name: {uploadedFile.name}</p>
                    <p>Description: {uploadedFile.description}</p>
                    <img
                        src={`http://localhost:5000/api/image/${uploadedFile.image}`}
                        alt={uploadedFile.name}
                        style={{ width: '300px', height: '300px' }}
                    />
                </section>
            )}
        </div>
    );
}

export default FoodFest;
