import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function Security() {
    // State variables for form inputs
    const [currentPassword, setCurrentPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState('');

    // Handle form submission
    const handleSubmit = async (event) => {
        event.preventDefault();

        if (newPassword !== confirmPassword) {
            setError('New password and confirm password do not match');
            return;
        }

        try {
            // Assume you have user ID stored in localStorage or some context
            const userId = localStorage.getItem('userId');

            // Make API request to update password
            const response = await axios.post(`http://localhost:5000/api/users/${userId}/update-password`, {
                currentPassword,
                newPassword
            });

            if (response.data.success) {
                alert('Password updated successfully');
            } else {
                setError(response.data.message);
            }
        } catch (err) {
            setError('An error occurred while updating password');
        }
    };

    return (
        <div>
            <div>
                <div className="container-xl px-4 mt-4">
                    <nav className="nav nav-borders">
                        <Link className="nav-link ms-0" to="/Setting">Profile</Link>
                        <Link className="nav-link active" to="/Security">Security</Link>
                    </nav>
                    <hr className="mt-0 mb-4" />
                    <div className="row">
                        <div className="col-lg-8">
                            <div className="card mb-4 bg-secondary">
                                <div className="card-header">Change Password</div>
                                <div className="card-body">
                                    <form onSubmit={handleSubmit}>
                                        <div className="mb-3">
                                            <label className="small mb-1" htmlFor="currentPassword">Current Password</label>
                                            <input
                                                className="form-control"
                                                id="currentPassword"
                                                type="password"
                                                placeholder="Enter current password"
                                                value={currentPassword}
                                                onChange={(e) => setCurrentPassword(e.target.value)}
                                            />
                                        </div>
                                        <div className="mb-3">
                                            <label className="small mb-1" htmlFor="newPassword">New Password</label>
                                            <input
                                                className="form-control"
                                                id="newPassword"
                                                type="password"
                                                placeholder="Enter new password"
                                                value={newPassword}
                                                onChange={(e) => setNewPassword(e.target.value)}
                                            />
                                        </div>
                                        <div className="mb-3">
                                            <label className="small mb-1" htmlFor="confirmPassword">Confirm Password</label>
                                            <input
                                                className="form-control"
                                                id="confirmPassword"
                                                type="password"
                                                placeholder="Confirm new password"
                                                value={confirmPassword}
                                                onChange={(e) => setConfirmPassword(e.target.value)}
                                            />
                                        </div>
                                        {error && <div className="alert alert-danger">{error}</div>}
                                        <button className="btn btn-primary" type="submit">Save</button>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Security;
